mapper.select:
select 
*
from (select * from KANBAN_DETAIL order by CREATE_DATE desc)
where 
SPACE_NO = #{spaceNo}
and STATUS = 'ToDo'

select *
from (select * from KANBAN_DETAIL order by CREATE_DATE desc)
where 
SPACE_NO = #{spaceNo}
and STATUS = 'Progress'

select *
from (select * from KANBAN_DETAIL order by CREATE_DATE desc)
where 
SPACE_NO = #{spaceNo}
and STATUS = 'Done'
